import React from "react";
import { Card, CardContent, Typography } from "@material-ui/core/Card";

function Message(props) {
  return (
    <div>
      <Card>
        <CardContent>
          <Typography color="white" variant="h5" component="h2">
            {props.text}
          </Typography>
        </CardContent>
      </Card>

      <h2>
        {props.username}: {props.text}
      </h2>
    </div>
  );
}

export default Message;
